import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function Productdetails({cartItems,SetcartItems}) {
  const [data, Setdata] = useState(null);
  const { id } = useParams();

  const [qty,setqty]=useState(1);

  useEffect(() => {
    fetch(process.env.REACT_APP_API + "/v1/products/getsingleproduct/" + id)
      .then((res) => res.json())
      .then((res) => Setdata(res.product));
  },[]);

  function addtoCart(){
   const itemexist=cartItems.find((items)=>items.data._id === data._id);
   if(!itemexist){
    const newItems = [data,qty]
    SetcartItems((items)=> [...items,newItems]);

   }

  }
  return (
    data && (
      <div class="container container-fluid">
        <div class="row f-flex justify-content-around">
          <div class="col-12 col-lg-5 img-fluid" id="product_image">
            <img
              src={data.images[0].image}
              alt="data.name"
              height="500"
              width="500"
            />
          </div>

          <div class="col-12 col-lg-5 mt-5">
            <h3>{data.name}</h3>
            <p id="product_id">Product {data._id}</p>

            <hr />

            <div class="rating-outer">
              <div class="rating-inner" style={{width:`${(data.rating/5)*100}%`}}></div>
            </div>

            <hr />

            <p id="product_price">${data.price}</p>
            <div class="stockCounter d-inline">
              <span class="btn btn-danger minus" onClick={()=>setqty((val)=> val<=1 ? val=1:val-1)}>-</span>

              <input
                type="number"
                class="form-control count d-inline"
                value={qty}
                readOnly
              />

              <span class="btn btn-primary plus" onClick={()=>setqty((val)=>val+1)}>+</span>
            </div>
            <button
            onClick={addtoCart}
              type="button"
              id="cart_btn"
              class="btn btn-primary d-inline ml-4"
            >
              Add to Cart
            </button>

            <hr />

            <p>
              Status: <span id="stock_status" className = {data.stock <= 0 ? 'text-danger':'text-success'}>{data.stock>0 ? 'In Stock':'Out of Stock'}</span>
            </p>

            <hr />

            <h4 class="mt-2">Description:</h4>
            <p>{data.description}</p>
            <hr />
            <p id="product_seller mb-3">
              Sold by: <strong>{data.seller}</strong>
            </p>

            <div class="rating w-50"></div>
          </div>
        </div>
      </div>
    )
  );
}
