import React, { Fragment, useCallback } from "react";
import { Link } from "react-router-dom";

export default function Cart({ cartItems, SetcartItems }) {
  console.log(cartItems);

  // Function to increase quantity
  const increaseqty = useCallback(
    (item) => {
      if (item.qty === item.product.stock) return;

      const newCartItems = cartItems.map((i) =>
        i.product._id === item.product._id
          ? { ...i, qty: i.qty + 1 } // Safely update qty
          : i
      );
      SetcartItems(newCartItems);
    },
    [cartItems, SetcartItems]
  );

  // Function to decrease quantity
  const decreaseqty = useCallback(
    (item) => {
      if (item.qty === 1) return;

      const newCartItems = cartItems.map((i) =>
        i.product._id === item.product._id
          ? { ...i, qty: i.qty - 1 }
          : i
      );
      SetcartItems(newCartItems);
    },
    [cartItems, SetcartItems]
  );

  // Function to remove an item from the cart
  const removeItem = useCallback(
    (id) => {
      const newCartItems = cartItems.filter((item) => item.product._id !== id);
      SetcartItems(newCartItems);
    },
    [cartItems, SetcartItems]
  );

  return (
    <div className="container container-fluid">
      <h2 className="mt-5">
        Your Cart: <b>{cartItems.length}</b>
      </h2>

      <div className="row d-flex justify-content-between">
        <div className="col-12 col-lg-8">
          <hr />
          {cartItems.map((item) => (
            <Fragment key={item.product._id}>
              <div className="cart-item">
                <div className="row">
                  <div className="col-4 col-lg-3">
                    <img
                      src={item.product.images[0].image}
                      alt={item.product.name}
                      height="90"
                      width="115"
                    />
                  </div>

                  <div className="col-5 col-lg-3">
                    <Link to={`/product/${item.product._id}`}>
                      {item.product.description}
                    </Link>
                  </div>

                  <div className="col-4 col-lg-2 mt-4 mt-lg-0">
                    <p id="card_item_price">${item.product.price}</p>
                  </div>

                  <div className="col-4 col-lg-3 mt-4 mt-lg-0">
                    <div className="stockCounter d-inline">
                      <button
                        className="btn btn-danger minus"
                        onClick={() => decreaseqty(item)}
                      >
                        -
                      </button>
                      <input
                        type="number"
                        className="form-control count d-inline"
                        value={item.qty}
                        readOnly
                      />
                      <button
                        className="btn btn-primary plus"
                        onClick={() => increaseqty(item)}
                      >
                        +
                      </button>
                    </div>
                  </div>

                  <div className="col-4 col-lg-1 mt-4 mt-lg-0">
                    <button
                      id="delete_cart_item"
                      className="btn btn-danger"
                      onClick={() => removeItem(item.product._id)}
                    >
                      <i className="fa fa-trash"></i>
                    </button>
                  </div>
                </div>
              </div>
              <hr />
            </Fragment>
          ))}
        </div>

        <div className="col-12 col-lg-3 my-4">
          <div id="order_summary">
            <h4>Order Summary</h4>
            <hr />
            <p>
              Subtotal:{" "}
              <span className="order-summary-values">
                {cartItems.reduce((acc, item) => acc + item.qty, 0)} (Units)
              </span>
            </p>
            <p>
              Est. total:{" "}
              <span className="order-summary-values">
                $
                {cartItems
                  .reduce((acc, item) => acc + item.qty * item.product.price, 0)
                  .toFixed(2)}
              </span>
            </p>
            <hr />
            <button id="checkout_btn" className="btn btn-primary btn-block">
              Place Order
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
